// Bootstrap specific functions and styling
jQuery(document).ready(function(){jQuery('.comment-reply-link').addClass('btn btn-sm btn-default');jQuery('#submit, button[type=submit], html input[type=button], input[type=reset], input[type=submit]').addClass('btn btn-default');jQuery('.widget_rss ul').addClass('media-list');jQuery('.postform').addClass('form-control');jQuery('table#wp-calendar').addClass('table table-striped');jQuery('#submit, .tagcloud, button[type=submit], .comment-reply-link, .widget_rss ul, .postform, table#wp-calendar').show("fast")});

// jQuery powered scroll to top
jQuery(document).ready(function(){jQuery(window).scroll(function(){jQuery(this).scrollTop()>=100?jQuery(".scroll-to-top").fadeIn():jQuery(".scroll-to-top").fadeOut()}),jQuery(".scroll-to-top").click(function(){return jQuery("html, body").animate({scrollTop:0},800),!1}),jQuery(".scroll").click(function(){return jQuery("html, body").animate({scrollTop:jQuery((jQuery(this).attr("href")||"").split("?")[0]).offset().top},"slow"),!1}),jQuery(".navbar-toggle").click(function(){jQuery(this).parent().toggleClass("open")})});

// Slider functions
jQuery(document).ready(function(e){e(window).load(function(){e(".flexslider").flexslider({animation:"fade",slideshowSpeed:7e3,smoothHeight:!0,touch:!0})})});